$o->data='Hello World';
//将自定义的归档元数据meta-data存入manifest
$phar->setMetadata($o);
//phar本质上是个压缩包，所以要添加压缩的文件和文件内容
$phar->addFromString("test.txt", "scerush");
//停止缓冲对phar的写操作
$phar->stopBuffering();
?>
